import React from 'react';
import './App.css';

class App extends React.Component {
constructor(props){
  super(props)
  this.state = {
    play: false
  };
  this.handleClick = this.handleClick.bind(this);
}

handleClick=()=>{
 this.setState({
  play: true
 })

}
  render() {
    return (
      <>
      <div id='drum-machine'>
        <div className='pads'>
          <button className="drum-pad" type='button' onClick={this.handleClick} >
          <audio className='clip' id='Q'>
              <source src='./Samples/[Kick] 05.wav'></source>
            </audio>Q</button>
          <button className="drum-pad" type='button' >
          <audio className='clip' id='Q'>
              <source src='./Samples/[Clap] 10.wav'></source>
            </audio>W</button>
          <button className="drum-pad" type='button' ><audio className='clip' id='Q'>
              <source src='./Samples/[CH] 04.wav'></source>
            </audio>E</button>
          <button className="drum-pad" type='button' ><audio className='clip' id='Q'>
              <source src='./Samples/[Rim] 04.wav'></source>
            </audio>A</button>
          <button className="drum-pad" type='button' ><audio className='clip' id='Q'>
              <source src='./Samples/[Perc] 01.wav'></source>
            </audio>S</button>
          <button className="drum-pad" type='button' ><audio className='clip' id='Q'>
              <source src='./Samples/[Snare] 07.wav'></source>
            </audio>D</button>
          <button className="drum-pad" type='button' ><audio className='clip' id='Q'>
              <source src='./Samples/[808] 06.wav'></source>
            </audio>Z</button>
          <button className="drum-pad" type='button' ><audio className='clip' id='Q'>
              <source src='./Samples/keys.wav'></source>
            </audio>X</button>
          <button className="drum-pad" type='button' ><audio className='clip' id='Q'>
              <source src='./Samples/[One-Shot] 14.wav'></source>
            </audio>C</button>
        </div>
        <div id='display'>

        </div>
      </div>  
      </>
    );
  }
}

export default App;
