import React from 'react';
import './App.css';

class App extends React.Component {
constructor(props){
  super(props)
  this.state = {
    play: false
  };
}
  render() {
    return (
      <>
      <div id='drum-machine'>
        <div className='pads'>
          <button className="drum-pad" type='button' >
          <audio className='clip' id='Q'>
              <source src='./Samples/[Kick] 05.wav'></source>
            </audio>Q</button>
          <button className="drum-pad" type='button' >W</button>
          <button className="drum-pad" type='button' >E</button>
          <button className="drum-pad" type='button' >A</button>
          <button className="drum-pad" type='button' >S</button>
          <button className="drum-pad" type='button' >D</button>
          <button className="drum-pad" type='button' >Z</button>
          <button className="drum-pad" type='button' >X</button>
          <button className="drum-pad" type='button' >C</button>
        </div>
        <div id='display'>

        </div>
      </div>  
      </>
    );
  }
}

export default App;
