import React from 'react';
import './App.css';

class App extends React.Component {
constructor(props){
  super(props)
  this.audio = React.createRef();
  this.handleClick = this.handleClick.bind(this);
  this.handleKeyPress =this.handleKeyPress.bind(this);
}

handleClick=()=>{
 this.audio.current.play()
 }


  render() {
    const sounds = [
      {
        keyCode: 81,
        key: 'Q',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/RP4_KICK_1.mp3'
      },
      {
        keyCode: 87,
        key: 'W',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Cev_H2.mp3'
      },
      {
        keyCode: 69,
        key: 'E',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-6.mp3'
      }, 
      {
        keyCode: 65,
        key: 'A',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Dsc_Oh.mp3'
      },
      {
        keyCode: 83,
        key: 'S',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-4_1.mp3'
      },
      {
        keyCode: 68,
        key: 'D',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-3.mp3'
      },
      {
        keyCode: 90,
        key: 'Z',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-2.mp3'
      },
      {
        keyCode: 88,
        key: 'X',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Kick_n_Hat.mp3'
      },
      {
        keyCode: 67,
        key: 'C',
        src: 'https://s3.amazonaws.com/freecodecamp/drums/Heater-1.mp3'
      }
    ]

   const  handleKeyPress=(event)=>{
      if (event.key === sounds.keyCode) {
        this.audio.current.play()
      }
    }



    return (
      <>
      <div id='drum-machine'>
        <div className='pads'>
          {sounds.map((pad,i) =>{
              return  <div className="drum-pad" type='button' onClick={this.handleClick} id={i} onKeyPress={this.handleKeyPress} >
              <audio ref={this.audio} className='clip' id={pad.key} src={pad.src} type="audio/wav"/>
              {pad.key}</div>
          })}
        </div>
        <div id='display'>

        </div>
      </div>  
      </>
    );
  }
}

export default App;
